// src/componentes/Paginas/users/register.js
import React, { useState } from 'react';

const Register = ({ onUserCreated }) => {
  const [userData, setUserData] = useState({
    nome: '',
    sobrenome: '',
    CPF: '',
    RG: '',
    dataRG: '',
    expeditorRG: '',
    senha: '',
    telefone: '',
    email: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({
      ...userData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch('http://localhost:3000/api/v1/users/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userData),
    });

    if (response.ok) {
      const newUser = await response.json();
      onUserCreated(newUser);
      setUserData({
        nome: '',
        sobrenome: '',
        CPF: '',
        RG: '',
        dataRG: '',
        expeditorRG: '',
        senha: '',
        telefone: '',
        email: '',
      });
    } else {
      alert('Erro ao cadastrar usuário');
    }
  };

  return (
    <div className="register-form">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="nome"
          placeholder="Nome"
          value={userData.nome}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="sobrenome"
          placeholder="Sobrenome"
          value={userData.sobrenome}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="CPF"
          placeholder="CPF"
          value={userData.CPF}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="RG"
          placeholder="RG"
          value={userData.RG}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="dataRG"
          placeholder="Data RG"
          value={userData.dataRG}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="expeditorRG"
          placeholder="Expeditor RG"
          value={userData.expeditorRG}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="senha"
          placeholder="Senha"
          value={userData.senha}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="telefone"
          placeholder="Telefone"
          value={userData.telefone}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={userData.email}
          onChange={handleChange}
          required
        />
        <button type="submit">Cadastrar</button>
      </form>
    </div>
  );
};

export default Register;
